mapper.update:
INSERT INTO MONITOR_VALUE (
data_time, device_flag, humidity, kid, location_id, quality10, quality25, temperature, volume
) VALUES (
#{data_time}, #{device_flag}, #{humidity}, #{kid}, #{location_id}, #{quality10}, #{quality25}, #{temperature}, #{volume}
)

INSERT INTO MONITOR_VALUE (
data_time, device_flag, humidity, kid, location_id, quality10, quality25, temperature, volume
) VALUES (
#{data_time}, #{device_flag}, #{humidity}, #{kid}, #{location_id}, #{quality10}, #{quality25}, #{temperature}, #{volume}
) ON DUPLICATE KEY UPDATE
data_time = #{data_time}, device_flag = #{device_flag}, humidity = #{humidity}, location_id = #{location_id}, quality10 = #{quality10}, quality25 = #{quality25}, temperature = #{temperature}, volume = #{volume}

INSERT IGNORE INTO MONITOR_VALUE (
data_time, device_flag, humidity, kid, location_id, quality10, quality25, temperature, volume
) VALUES
(
#{item.data_time}, #{item.device_flag}, #{item.humidity}, #{item.kid}, #{item.location_id}, #{item.quality10}, #{item.quality25}, #{item.temperature}, #{item.volume}
)
ON DUPLICATE KEY UPDATE
data_time = values(data_time), device_flag = values(device_flag), humidity = values(humidity), kid = values(kid), location_id = values(location_id), quality10 = values(quality10), quality25 = values(quality25), temperature = values(temperature), volume = values(volume)

INSERT INTO MONITOR_VALUE (
data_time, device_flag, humidity, kid, location_id, quality10, quality25, temperature, volume
) VALUES
(
#{item.data_time}, #{item.device_flag}, #{item.humidity}, #{item.kid}, #{item.location_id}, #{item.quality10}, #{item.quality25}, #{item.temperature}, #{item.volume}
)

UPDATE MONITOR_VALUE
data_time = #{data_time}, device_flag = #{device_flag}, humidity = #{humidity}, location_id = #{location_id}, quality10 = #{quality10}, quality25 = #{quality25}, temperature = #{temperature}, volume = #{volume}
WHERE kid = #{kid} LIMIT 1

mapper.select:
SELECT mv.kid, ln.name locationName, se.name siteName, dne.flag_name flagName, mv.device_flag, mv.volume, mv.temperature, mv.quality25, mv.quality10, DATE_FORMAT(mv.create_time,'%Y-%m-%d %H:%i:%s') create_time FROM MONITOR_VALUE mv left join location ln on ln.kid = mv.location_id left join site se on se.kid = ln.site_id left join device_noise dne on dne.kid = ln.device_id
ORDER BY ${column} ${order}
ORDER BY mv.create_time DESC
LIMIT #{section},#{pageSize}

select se.name siteName,LEFT(avg(mv.volume),5) count,se.lng,se.lat,se.kid from ( select mv.location_id,max(mv.create_time) max from monitor_value mv group by mv.location_id) lt left join monitor_value mv on mv.location_id = lt.location_id and lt.max = mv.create_time left join location ln on ln.kid = mv.location_id left join site se on se.kid = ln.site_id
and se.kid in
#{item}
and ln.county_id in
#{item}
group by se.name,se.lng,se.lat,se.kid
having avg(mv.volume) >= #{min} and avg(mv.volume) < #{max}
having avg(mv.volume) >= #{min}
ORDER BY se.name limit 24

select ln.device_id,dne.flag_name,ln.longs,ln.lat,DATE_FORMAT(max(mv.create_time),'%Y-%m-%d %H:%i:%s') createtime, IF(timestampdiff(SECOND,now(),max(mv.create_time)) < -120,0,1) status from location ln left join monitor_value mv on mv.location_id = ln.kid left join device_noise dne on dne.kid = ln.device_id
and ln.site_id in
#{item}
and ln.county_id in
#{item}
group by ln.device_id,dne.flag_name,ln.longs,ln.lat
